        <td>Ulica i broj</td>
        <td><input type="text" [(ngModel)]="ulica"></td>
    </tr>
    <tr *ngIf="tip=='agencija'">
        <td>Grad</td>
        <td><input type="text" [(ngModel)]="grad"></td>
    </tr>
    <tr *ngIf="tip=='agencija'">
        <td>Drzava</td>
        <td><input type="text" [(ngModel)]="drzava"></td>
    </tr>
    <tr *ngIf="tip=='agencija'">
        <td>Maticni broj agencije</td>
        <td><input type="text" [(ngModel)]="maticniBroj"></td>
    </tr>
    <tr *ngIf="tip=='agencija'">
        <td>Kratak opis agencije</td>
        <td><textarea [(ngModel)]="opis"></textarea></td>
    </tr>
    <tr>
        <td>Profilna slika</td>
        <td><input type="file" (change)="otpremanjeFajla($event)"></td>
    </tr>
    <tr>
        <td colspan="2"><button (click)="registracijaa()">Registruj se</button></td>
    </tr>
</table>
