<div class="container">
  <div class="row justify-content-center">
    <div class="col-lg-4 col-md-6 col-sm-8">
      <h1 class="text-center">Prijava u DomIzSnova</h1>
      <div class="card">
        <div class="card-body">
          <div class="form-group">
            <label for="korisnickoIme">Korisničko ime</label>
            <input type="text" class="form-control" name="korisnickoIme" id="korisnickoIme" [(ngModel)]="korisnickoIme">
          </div>
          <div class="form-group">
            <label for="lozinka">Lozinka</label>
            <input type="password" class="form-control" name="lozinka" id="lozinka" [(ngModel)]="lozinka">
          </div>
          <div class="form-group text-center">
            <a class="forgot-password-link" [routerLink]="['/zaboravljenaLozinka']">Zaboravljena lozinka?</a>
          </div>
          <div class="form-group text-center">
            <button class="btn btn-primary" (click)="prijava()">Prijava</button>
          </div>
        </div>
      </div>
      <div class="text-center mt-3">
        Niste prijavljeni? <a [routerLink]="['/registracija']">Registruj se</a>
      </div>
      <div class="text-center text-danger">{{ greska }}</div>
    </div>
  </div>
</div>
