import { Component, OnInit } from '@angular/core';
import { RadnikService } from '../services/radnik.service';
import { Radnik } from '../models/radnik';
import { Agencija } from '../models/agencija';
import { AgencijaService } from '../services/agencija.service';
import { ProveraService } from '../services/provera.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-radnici-agencija',
  templateUrl: './radnici-agencija.component.html',
  styleUrls: ['./radnici-agencija.component.css'],
})
export class RadniciAgencijaComponent implements OnInit {
  constructor(
    private radnikServis: RadnikService,
    private agencijaServis: AgencijaService,
    private proveraServis: ProveraService,
    private ruter: Router
  ) {}

  ngOnInit(): void {
    let korisnickoIme = sessionStorage.getItem('korisnik');

    this.agencijaServis
      .dohvatiAgenciju(korisnickoIme)
      .subscribe((agencija: Agencija) => {
        this.agencija = agencija;
      });

    this.radnikServis
      .dohvatiRadnikeAgencije(korisnickoIme)
      .subscribe((radnici: Radnik[]) => {
        this.radnici = radnici;
      });
  }

  obrisiRadnika(radnik: Radnik) {
    this.radnikServis.obrisiRadnika(radnik._id).subscribe((radnik: Radnik) => {
      this.radnikServis
        .dohvatiRadnikeAgencije(this.agencija.korisnickoIme)
        .subscribe((radnici: Radnik[]) => {
          this.radnici = radnici;
        });
    });
  }

  izmeniRadnika(radnik: Radnik) {
    this.radnikServis.azurirajRadnika(radnik).subscribe((radnik: Radnik) => {
      this.radnikServis
        .dohvatiRadnikeAgencije(this.agencija.korisnickoIme)
        .subscribe((radnici: Radnik[]) => {
          this.radnici = radnici;
        });
    });
    this.rezimIzmene = false;
  }

  async dodajRadnika() {
    const radnik = {
      telefon: this.telefon,
      mejl: this.mejl,
      ime: this.ime,
      prezime: this.prezime,
    };

    let provera = await this.proveraServis.proveraRadnik(radnik);
    if (provera != 'ok') {
      this.greska = provera;
      return;
    }
    this.radnikServis
      .dodajRadnika(
        this.ime,
        this.prezime,
        this.mejl,
        this.telefon,
        this.specijalizacija,
        this.agencija.korisnickoIme
      )
      .subscribe((radnik: Radnik) => {
        alert('Uspesno ste dodali radnika!');
        this.radnikServis
          .dohvatiRadnikeAgencije(this.agencija.korisnickoIme)
          .subscribe((radnici: Radnik[]) => {
            this.radnici = radnici;
          });
      });

    this.ime = '';
    this.prezime = '';
    this.mejl = '';
    this.telefon = '';
    this.specijalizacija = '';
    this.greska = '';
  }

  posaljiZahtev() {
    if (this.agencija.zahtev != 0) {
      alert(
        'Vec ste poslali zahtev za prosirenje broja radnih mesta!\nAdministratori ce uskoro razmotriti vas zahtev'
      );
      return;
    }
    this.agencijaServis
      .azurirajPodatak(this.agencija.korisnickoIme, 'zahtev', this.prosirenje)
      .subscribe((res) => {
        alert('Uspesno ste poslali zahtev za povecanje broja radnih mesta!');
        this.prosirenje = 0;
      });
  }

  odjava() {
    sessionStorage.clear();
    this.ruter.navigate(['/']);
  }

  radnici: Radnik[] = [];
  agencija: Agencija = new Agencija();

  rezimIzmene: boolean = false;

  ime: string = '';
  prezime: string = '';
  mejl: string = '';
  telefon: string = '';
  specijalizacija: string = '';

  prosirenje: number = 0;

  greska: string = '';
}
<app-zaglavlje-agencija></app-zaglavlje-agencija>
<div class="container">
  <div class="row">
    <div class="col-md-12">
      <h4>Broj radnika: {{ radnici.length }}</h4>
      <h4>Kapacitet radnika: {{ agencija.kapacitetRadnika }}</h4>
      <hr>
    </div>
  </div>

  <table *ngIf="radnici.length > 0" class="table table-striped">
    <thead>
      <tr>
        <th>Ime</th>
        <th>Prezime</th>
        <th>Broj telefona</th>
        <th>Mejl</th>
        <th>Specijalizacija</th>
        <th colspan="2">Akcije</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let r of radnici">
        <td><input type="text" class="form-control" [(ngModel)]="r.ime" [disabled]="!rezimIzmene"></td>
        <td><input type="text" class="form-control" [(ngModel)]="r.prezime" [disabled]="!rezimIzmene"></td>
        <td><input type="text" class="form-control" [(ngModel)]="r.telefon" [disabled]="!rezimIzmene"></td>
        <td><input type="text" class="form-control" [(ngModel)]="r.mejl" [disabled]="!rezimIzmene"></td>
        <td><input type="text" class="form-control" [(ngModel)]="r.specijalizacija" [disabled]="!rezimIzmene"></td>
        <td><button class="btn btn-danger" (click)="obrisiRadnika(r)" *ngIf="!rezimIzmene">Obrisi</button></td>
        <td><button class="btn btn-primary" (click)="izmeniRadnika(r)" *ngIf="rezimIzmene">Potvrdi izmene</button></td>
      </tr>
    </tbody>
    <tfoot>
      <tr>
        <td colspan="7">
          <input type="checkbox" id="cbox1" [(ngModel)]="rezimIzmene">
          <label for="cbox1">Režim izmene</label>
        </td>
      </tr>
    </tfoot>
  </table>

  <p *ngIf="radnici.length == 0">Trenutno nemate radnika</p>
  <hr>

  <div class="row">
    <div class="col-md-6">
      <h2>Dodaj radnika</h2>
      <table class="table">
        <tr>
          <td>Ime</td>
          <td><input type="text" class="form-control" [(ngModel)]="ime"></td>
        </tr>
        <tr>
          <td>Prezime</td>
          <td><input type="text" class="form-control" [(ngModel)]="prezime"></td>
        </tr>
        <tr>
          <td>Broj telefona</td>
          <td><input type="text" class="form-control" [(ngModel)]="telefon"></td>
        </tr>
        <tr>
          <td>Mejl</td>
          <td><input type="text" class="form-control" [(ngModel)]="mejl"></td>
        </tr>
        <tr>
          <td>Specijalizacija</td>
          <td><input type="text" class="form-control" [(ngModel)]="specijalizacija"></td>
        </tr>
        <tr>
          <td colspan="2"><button class="btn btn-primary" (click)="dodajRadnika()">Dodaj radnika</button></td>
        </tr>
      </table>
      <hr>
    </div>

    <div class="col-md-6">
      <h2>Zahtevaj proširenje kapaciteta radnika</h2>
      <div class="row">
          Zahtevaj još <input type="number" class="form-control" [(ngModel)]="prosirenje"> radnika
        <div class="row">
          <button class="btn btn-primary" (click)="posaljiZahtev()">Pošalji zahtev</button>
        </div>
      </div>
    </div>
  </div>

  <div class="row">
    <div class="col-md-12">
      <p class="text-danger">{{ greska }}</p>
    </div>
  </div>
</div>
